"use strict";(self.webpackChunktwo=self.webpackChunktwo||[]).push([[787],{9588:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"/3_6_7","metadata":{"permalink":"/de/3_6_7","source":"@site/i18n/de/docusaurus-plugin-content-blog/3_6_7/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.7 Update-Hinweise","description":"Videoqualit\xe4t freischalten","date":"2022-04-27T00:00:00.000Z","formattedDate":"27. April 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-27T00:00:00.000Z"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.6.6 Update-Hinweise","permalink":"/de/3_6_6"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityUnlock from \'./qualityUnlock.png\';\\n\\n\\n\\n\\n### Videoqualit\xe4t freischalten\\n\\nJetzt wird die Standardsperre der Videoqualit\xe4t aufgehoben, und Sie k\xf6nnen den Status durch Klicken wechseln, und Sie m\xfcssen die Seite nach dem Wechseln des Status aktualisieren.\\n\\n<img src={qualityUnlock} alt=\\"qualityUnlock.png\\" width=\\"330\\" height=\\"320\\" />\\n\\n<WebStore />"},{"id":"/3_6_6","metadata":{"permalink":"/de/3_6_6","source":"@site/i18n/de/docusaurus-plugin-content-blog/3_6_6/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.6 Update-Hinweise","description":"### Videoqualit\xe4tssperre beheben","date":"2022-04-11T00:00:00.000Z","formattedDate":"11. April 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-11T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.7 Update-Hinweise","permalink":"/de/3_6_7"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.6.5 Update-Hinweise","permalink":"/de/3_6_5"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityLock from \'./qualityLock.png\';\\nimport defaultStyle from \'./defaultStyle.png\';\\n\\n\\n\\n\\n ### Videoqualit\xe4tssperre beheben\\n\\n Die Funktion zum Sperren der Videoqualit\xe4t wurde in Version 3.6.3 bereitgestellt, aber wenn Auto ausgew\xe4hlt ist, ist die Qualit\xe4t immer noch gesperrt und kann nicht dynamisch angepasst werden.Dieses Problem ist jetzt behoben.\\n\\n<img src={qualityLock} alt=\\"qualityLock.png\\" width=\\"276\\" height=\\"389\\" />\\n\\n### Standardstil f\xfcr Untertitel wiederherstellen\\n\\nVoreingestellte Untertitelstile wurden in fr\xfcheren Versionen bereitgestellt, und der Standardstil f\xfcr Untertitel ist jetzt konsistent mit YouTube.\\n\\nWenn Sie zum Standardstil zur\xfcckkehren m\xfcssen, k\xf6nnen Sie auf die Schaltfl\xe4che Stil zur\xfccksetzen klicken.\\n\\n<img src={defaultStyle} alt=\\"defaultStyle.png\\" width=\\"340\\" height=\\"524\\" />\\n\\n<WebStore />"},{"id":"/3_6_5","metadata":{"permalink":"/de/3_6_5","source":"@site/i18n/de/docusaurus-plugin-content-blog/3_6_5/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.5 Update-Hinweise","description":"### Untertitel wurden nicht umbrochen","date":"2022-04-07T00:00:00.000Z","formattedDate":"7. April 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-07T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.6 Update-Hinweise","permalink":"/de/3_6_6"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.6.0 Update-Hinweise","permalink":"/de/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport improveHandle from \'./improveHandle.gif\';\\nimport subtitleRightClickMenu from \'./subtitleRightClickMenu.gif\';\\nimport subtitleTips from \'./subtitleTips.png\';\\nimport subtitleWrap from \'./subtitleWrap.png\';\\n\\n\\n\\n\\n ### Untertitel wurden nicht umbrochen\\n\\n Es wurde ein Problem behoben, das dazu f\xfchrte, dass Untertitel ohne Umbruch \xfcberliefen, wenn die Schriftart in der vorherigen Version zu gro\xdf war.\\n\\n<img src={subtitleWrap} alt=\\"subtitleWrap.png\\" width=\\"600\\" height=\\"698\\" />\\n\\n### Untertitel-Eingabeaufforderung korrigieren\\n\\nIn fr\xfcheren Versionen verwendet die Untertitelaufforderung auch den Stil, und jetzt wird dieses Problem vermieden.\\n\\n<img src={subtitleTips} alt=\\"subtitleTips.png\\" width=\\"600\\" height=\\"682\\" />\\n\\n### Griff verbessern\\n\\nDie Griff-Schaltfl\xe4chen waren fr\xfcher schwierig zu ziehen, wenn der Untertitel an den Rand gezogen wurde, jetzt ist es einfacher, ihn zu verwenden, indem man die Griffposition \xe4ndert.\\n\\n<img src={improveHandle} alt=\\"improveHandle.gif\\" width=\\"600\\" height=\\"338\\" />\\n\\n### Rechtsklickmen\xfc f\xfcr Untertitel\\n\\nIn der Vergangenheit konnten Untertitel kopiert werden, aber Rechtsklick-Operationen konnten nicht ausgef\xfchrt werden.Jetzt wurde dieses Problem behoben.\\n\\n<img src={subtitleRightClickMenu} alt=\\"subtitleRightClickMenu.gif\\" width=\\"600\\" height=\\"313\\" />\\n\\n<WebStore />"},{"id":"/3_6_0","metadata":{"permalink":"/de/3_6_0","source":"@site/i18n/de/docusaurus-plugin-content-blog/3_6_0/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.0 Update-Hinweise","description":"Zusammenf\xfchrung von Anweisungen behoben \ud83c\udf89","date":"2021-12-16T00:00:00.000Z","formattedDate":"16. Dezember 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-12-16T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.5 Update-Hinweise","permalink":"/de/3_6_5"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.5.0 Update-Hinweise","permalink":"/de/3_5_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport mark from \'./mark.png\';\\nimport disableStyle from \'./disableStyle.gif\';\\nimport order from \'./order.gif\';\\n\\n\\n### Zusammenf\xfchrung von Anweisungen behoben \ud83c\udf89\\n\\n In der vorherigen Version wurde das Zusammenf\xfchren von Aussagen nicht durchgef\xfchrt, um das eigene Verhalten von YouTube zu verfolgen.\\n\\nDies f\xfchrte dazu, dass die S\xe4tze beim Abspielen des Videos abgehackt waren und das Herunterladen von Untertiteln auch dazu f\xfchrte, dass die Timeline un\xfcbersichtlich wurde.\\n\\nIn Version 3.6.0 wurde dieses Problem behoben und weitere Details optimiert.\\n\\nZus\xe4tzliche Leerzeichen in Zusammenf\xfchrungsanweisungen entfernt.\\n\\n```\\n 1\\n 00:00:03,678 --\x3e 00:00:05,960\\n means that many aspects  of our lives\\n \u610f\u5473\u7740 \u6211\u4eec\u751f\u6d3b\u7684\u8bb8\u591a\u65b9\u9762\\n\\n 2\\n 00:00:05,960 --\x3e 00:00:08,214are regulated  by a weird entity.\\n \u90fd \u53d7\u5230\u4e00\u4e2a\u5947\u602a\u7684\u5b9e\u4f53\u7684\u76d1\u7ba1\u3002\\n\\n 3\\n 00:00:09,050 --\x3e 00:00:13,453It feels like a huge bureaucracy is making decisions over our heads.\\n \u611f\u89c9\u5c31\u50cf\u4e00\u4e2a\u5de8\u5927\u7684\u5b98\u50da\u673a\u6784 \u6b63\u5728\u6211\u4eec\u7684\u5934\u8111\u4e2d\u505a\u51fa\u51b3\u5b9a\u3002\\n ```\\n\\nLeere Zeitachse zum Herunterladen von Untertiteln entfernt.\\n\\n```\\n 1\\n 00:00:00,000 --\x3e 00:00:00,500\\n\\n\\n\\n 2\\n 00:00:00,500 --\x3e 00:00:03,090\\n PETE LEPAGE: You can now upload extensions using Manifest\\n PETE LEPAGE: \u60a8\u73b0\u5728\u53ef\u4ee5 \u4f7f\u7528 Manifest V3 \u5c06\u6269\u5c55\u7a0b\u5e8f\u4e0a\u4f20\\n\\n 3\\n 00:00:03,090 --\x3e 00:00:05,100\\n V3 to the Chrome Web Store.\\n \u5230 Chrome \u7f51\u4e0a\u5e94\u7528\u5e97\u3002\\n ```\\n\\n### Zweifache Untertitel geben Videountertiteln Vorrang\\n\\n Fr\xfcher wurden zweisprachige Untertitel immer maschinell \xfcbersetzt, jetzt werden zuerst die vorhandenen Untertitel des Videos verwendet, und wenn kein Untertitel vorhanden ist, wird die maschinelle \xdcbersetzung verwendet.\\n\\n Wenn das zweisprachige Video bereits Untertitel hat, wird ein Optimierungshinweis angezeigt.\\n\\n Das Herunterladen von Untertiteln hat ebenfalls die gleiche Optimierung, mit einem H\xe4kchen in der Mitte des Dateinamens.\\n\\n<Tip>Das Logo zeigt an, dass die aktuellen zweisprachigen Untertitel die vorhandenen Untertitel des Videos verwenden und keine maschinelle \xdcbersetzung.</Tip>\\n\\n<img src={mark} alt=\\"mark.png\\" width=\\"891\\" height=\\"262\\" />\\n\\n### Benutzerdefinierte Stile deaktivieren\\n\\n Diese Version bietet die M\xf6glichkeit, den benutzerdefinierten Stil zu deaktivieren, und kann die Reihenfolge und den Abstand der Untertitel unabh\xe4ngig voneinander festlegen.\\n\\n<Tip>Tipp: Die Pr\xe4misse dieser Optimierung ist, dass die beiden Untertitel genau \xfcbereinstimmen m\xfcssen.</Tip>\\n\\n<img src={disableStyle} alt=\\"disableStyle.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n### Laden Sie Untertitel in derselben Reihenfolge herunter, in der sie angezeigt werden\\n\\n<img src={order} alt=\\"order.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n<WebStore />"},{"id":"/3_5_0","metadata":{"permalink":"/de/3_5_0","source":"@site/i18n/de/docusaurus-plugin-content-blog/3_5_0/index.mdx","title":"YouTube\u2122 Dual Caption 3.5.0 Update-Hinweise","description":"Benutzerdefinierter Schriftstil","date":"2021-08-24T00:00:00.000Z","formattedDate":"24. August 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-08-24T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.0 Update-Hinweise","permalink":"/de/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport light from \'./light.png\';\\nimport dark from \'./dark.png\';\\nimport tip from \'./tip.gif\';\\nimport singlebutton from \'./singlebutton.png\';\\nimport single from \'./single.png\';\\nimport translationError from \'./translationError.gif\';\\n\\n\\n### Benutzerdefinierter Schriftstil\\n\\nKlicken Sie auf das Erweiterungssymbol, um die Einstellungsseite zur Unterst\xfctzung des dunklen Modus anzuzeigen.\\n\\nBenutzerdefinierte Optionen: Schriftart, Schriftgr\xf6\xdfe, Farbe, Hintergrundfarbe, Abstand und Reihenfolge der Untertitel.\\n\\n<img src={light} alt=\\"light.png\\" width=\\"340\\" height=\\"472\\" />\\n<img src={dark} alt=\\"dark.png\\" width=\\"340\\" height=\\"472\\" />\\n\\n<Tip>Wenn Sie den Mauszeiger \xfcber das Symbol bewegen, wird angezeigt, was die Einstellung darstellt.</Tip>\\n\\n<img src={tip} alt=\\"tip.gif\\" width=\\"340\\" height=\\"472\\" />\\n\\n### Einzelner Untertitelstil\\n\\nNach dem Umschalten in den Einzeluntertitelmodus kann der Einzeluntertitelstil unabh\xe4ngig eingestellt werden.\\n\\n<img src={singlebutton} alt=\\"singlebutton.png\\" width=\\"314\\" height=\\"372\\" />\\n<img src={single} alt=\\"single.png\\" width=\\"340\\" height=\\"406\\" />\\n\\n### Andere Optimierungen\\n\\nEntfernen Sie die Programmbibliothek von Chinese Traditional to Simplified, reduzieren Sie die Programmgr\xf6\xdfe und verbessern Sie die Leistung, da YouTube diesen Fehler offiziell behoben hat.\\n\\n<Tip>Manchmal fehlen Untertitel, meist aufgrund von YouTube-\xdcbersetzungsfehlern.</Tip>\\n\\n<img src={translationError} alt=\\"translationError.gif\\" width=\\"700\\" height=\\"396\\" />\\n\\nIn Zukunft werden andere \xdcbersetzungsmaschinen eingef\xfchrt, um dieses Problem zu l\xf6sen und \xdcbersetzungsungenauigkeiten auszugleichen.\\n\\n<WebStore />"}]}')}}]);