"use strict";(self.webpackChunktwo=self.webpackChunktwo||[]).push([[164],{4044:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"/3_6_7","metadata":{"permalink":"/Dual-Caption-Blog/en/3_6_7","source":"@site/i18n/en/docusaurus-plugin-content-blog/3_6_7/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.7 Update Notes","description":"Unlock video quality","date":"2022-04-27T00:00:00.000Z","formattedDate":"April 27, 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-27T00:00:00.000Z"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.6.6 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_6"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityUnlock from \'./qualityUnlock.png\';\\n\\n\\n\\n\\n### Unlock video quality\\n\\nNow the default lock of video quality is released, and you can switch states by clicking, and you need to refresh the page after switching states.\\n\\n<img src={qualityUnlock} alt=\\"qualityUnlock.png\\" width=\\"330\\" height=\\"320\\" />\\n\\n<WebStore />"},{"id":"/3_6_6","metadata":{"permalink":"/Dual-Caption-Blog/en/3_6_6","source":"@site/i18n/en/docusaurus-plugin-content-blog/3_6_6/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.6 Update Notes","description":"### Fix video quality lock","date":"2022-04-11T00:00:00.000Z","formattedDate":"April 11, 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-11T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.7 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_7"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.6.5 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_5"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityLock from \'./qualityLock.png\';\\nimport defaultStyle from \'./defaultStyle.png\';\\n\\n\\n\\n\\n ### Fix video quality lock\\n\\n The video quality lock function was provided in version 3.6.3, but when Auto is selected, the quality is still locked and cannot be adjusted dynamically. This problem is now fixed.\\n\\n<img src={qualityLock} alt=\\"qualityLock.png\\" width=\\"276\\" height=\\"389\\" />\\n\\n### Restore subtitle default style\\n\\nPreset subtitle styles were provided in previous versions, and the subtitle default style is now consistent with YouTube.\\n\\nIf you need to revert to the default style, you can click the Reset Style button.\\n\\n<img src={defaultStyle} alt=\\"defaultStyle.png\\" width=\\"340\\" height=\\"524\\" />\\n\\n<WebStore />"},{"id":"/3_6_5","metadata":{"permalink":"/Dual-Caption-Blog/en/3_6_5","source":"@site/i18n/en/docusaurus-plugin-content-blog/3_6_5/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.5 Update Notes","description":"### Fix subtitles not wrapping","date":"2022-04-07T00:00:00.000Z","formattedDate":"April 7, 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-07T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.6 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_6"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.6.0 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport improveHandle from \'./improveHandle.gif\';\\nimport subtitleRightClickMenu from \'./subtitleRightClickMenu.gif\';\\nimport subtitleTips from \'./subtitleTips.png\';\\nimport subtitleWrap from \'./subtitleWrap.png\';\\n\\n\\n\\n\\n ### Fix subtitles not wrapping\\n\\n Fixed an issue that caused subtitles to overflow without wrapping when the font was too large in the previous version.\\n\\n<img src={subtitleWrap} alt=\\"subtitleWrap.png\\" width=\\"600\\" height=\\"698\\" />\\n\\n### Fix subtitle prompt\\n\\nIn previous versions, the subtitle prompt will also use the style, and now this problem is avoided.\\n\\n<img src={subtitleTips} alt=\\"subtitleTips.png\\" width=\\"600\\" height=\\"682\\" />\\n\\n### Improve the handle\\n\\nThe handle buttons used to be difficult to drag when the subtitle was dragged to the border, now it is easier to use by changing the handle position.\\n\\n<img src={improveHandle} alt=\\"improveHandle.gif\\" width=\\"600\\" height=\\"338\\" />\\n\\n### Subtitle right-click menu\\n\\nIn the past, subtitles could be copied, but right-click operation could not be performed. Now this problem has been improved.\\n\\n<img src={subtitleRightClickMenu} alt=\\"subtitleRightClickMenu.gif\\" width=\\"600\\" height=\\"313\\" />\\n\\n<WebStore />"},{"id":"/3_6_0","metadata":{"permalink":"/Dual-Caption-Blog/en/3_6_0","source":"@site/i18n/en/docusaurus-plugin-content-blog/3_6_0/index.mdx","title":"YouTube\u2122 Dual Caption 3.6.0 Update Notes","description":"Fix statement merging \ud83c\udf89","date":"2021-12-16T00:00:00.000Z","formattedDate":"December 16, 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-12-16T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.5 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_5"},"nextItem":{"title":"YouTube\u2122 Dual Caption 3.5.0 Update Notes","permalink":"/Dual-Caption-Blog/en/3_5_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport mark from \'./mark.png\';\\nimport disableStyle from \'./disableStyle.gif\';\\nimport order from \'./order.gif\';\\n\\n\\n### Fix statement merging \ud83c\udf89\\n\\n In the previous version, statement merging was not done in order to follow YouTube\'s own behavior.\\n\\nThis caused the sentences to be choppy when playing the video, and downloading subtitles also caused the timeline to be cluttered.\\n\\nIn version 3.6.0, this problem has been fixed and more details have been optimized.\\n\\nRemoved extra spaces in merge statements.\\n\\n```\\n 1\\n 00:00:03,678 --\x3e 00:00:05,960\\n means that many aspects  of our lives\\n \u610f\u5473\u7740 \u6211\u4eec\u751f\u6d3b\u7684\u8bb8\u591a\u65b9\u9762\\n\\n 2\\n 00:00:05,960 --\x3e 00:00:08,214are regulated  by a weird entity.\\n \u90fd \u53d7\u5230\u4e00\u4e2a\u5947\u602a\u7684\u5b9e\u4f53\u7684\u76d1\u7ba1\u3002\\n\\n 3\\n 00:00:09,050 --\x3e 00:00:13,453It feels like a huge bureaucracy is making decisions over our heads.\\n \u611f\u89c9\u5c31\u50cf\u4e00\u4e2a\u5de8\u5927\u7684\u5b98\u50da\u673a\u6784 \u6b63\u5728\u6211\u4eec\u7684\u5934\u8111\u4e2d\u505a\u51fa\u51b3\u5b9a\u3002\\n ```\\n\\nRemoved blank timeline for downloading subtitles.\\n\\n```\\n 1\\n 00:00:00,000 --\x3e 00:00:00,500\\n\\n\\n\\n 2\\n 00:00:00,500 --\x3e 00:00:03,090\\n PETE LEPAGE: You can now upload extensions using Manifest\\n PETE LEPAGE: \u60a8\u73b0\u5728\u53ef\u4ee5 \u4f7f\u7528 Manifest V3 \u5c06\u6269\u5c55\u7a0b\u5e8f\u4e0a\u4f20\\n\\n 3\\n 00:00:03,090 --\x3e 00:00:05,100\\n V3 to the Chrome Web Store.\\n \u5230 Chrome \u7f51\u4e0a\u5e94\u7528\u5e97\u3002\\n ```\\n\\n### Dual subtitles give priority to video subtitles\\n\\n In the past, bilingual subtitles were always machine-translated. Now, the existing subtitles of the video will be used first, and machine translation will be used if there is no subtitle.\\n\\n If the bilingual video already has subtitles, an optimization indicator will be displayed.\\n\\n Downloading subtitles will also have the same optimization, with a tick in the middle of the filename.\\n\\n<Tip>The logo indicates that the current bilingual subtitles use the existing subtitles of the video, not machine translation.</Tip>\\n\\n<img src={mark} alt=\\"mark.png\\" width=\\"891\\" height=\\"262\\" />\\n\\n### Turn off custom styles\\n\\n This version provides the option to turn off the custom style, and can set the subtitle order and spacing independently.\\n\\n<Tip>Tip: The premise of this optimization is that the two subtitles must match exactly.</Tip>\\n\\n<img src={disableStyle} alt=\\"disableStyle.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n### Download subtitles in the same order as they are displayed\\n\\n<img src={order} alt=\\"order.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n<WebStore />"},{"id":"/3_5_0","metadata":{"permalink":"/Dual-Caption-Blog/en/3_5_0","source":"@site/i18n/en/docusaurus-plugin-content-blog/3_5_0/index.mdx","title":"YouTube\u2122 Dual Caption 3.5.0 Update Notes","description":"Custom font style","date":"2021-08-24T00:00:00.000Z","formattedDate":"August 24, 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-08-24T00:00:00.000Z"},"prevItem":{"title":"YouTube\u2122 Dual Caption 3.6.0 Update Notes","permalink":"/Dual-Caption-Blog/en/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport light from \'./light.png\';\\nimport dark from \'./dark.png\';\\nimport tip from \'./tip.gif\';\\nimport singlebutton from \'./singlebutton.png\';\\nimport single from \'./single.png\';\\nimport translationError from \'./translationError.gif\';\\n\\n\\n### Custom font style\\n\\nClick the extension icon to pop up the settings page to support dark mode.\\n\\nCustom options: font, font size, color, background color, spacing, and subtitle order.\\n\\n<img src={light} alt=\\"light.png\\" width=\\"340\\" height=\\"472\\" />\\n<img src={dark} alt=\\"dark.png\\" width=\\"340\\" height=\\"472\\" />\\n\\n<Tip>Hovering over the icon will reveal what the setting represents.</Tip>\\n\\n<img src={tip} alt=\\"tip.gif\\" width=\\"340\\" height=\\"472\\" />\\n\\n### Single subtitle style\\n\\nAfter switching to single subtitle mode, the single subtitle style can be set independently.\\n\\n<img src={singlebutton} alt=\\"singlebutton.png\\" width=\\"314\\" height=\\"372\\" />\\n<img src={single} alt=\\"single.png\\" width=\\"340\\" height=\\"406\\" />\\n\\n### Other optimizations\\n\\nRemove the Chinese Traditional to Simplified program library, reduce program size and improve performance, because YouTube has officially fixed this bug.\\n\\n<Tip>Sometimes subtitles are missing, mostly due to YouTube translation errors.</Tip>\\n\\n<img src={translationError} alt=\\"translationError.gif\\" width=\\"700\\" height=\\"396\\" />\\n\\nIn the future, other translation engines will be introduced to solve this problem and make up for translation inaccuracy.\\n\\n<WebStore />"}]}')}}]);