"use strict";(self.webpackChunktwo=self.webpackChunktwo||[]).push([[328],{6687:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"/3_6_7","metadata":{"permalink":"/fr/3_6_7","source":"@site/i18n/fr/docusaurus-plugin-content-blog/3_6_7/index.mdx","title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.7","description":"D\xe9verrouiller la qualit\xe9 vid\xe9o","date":"2022-04-27T00:00:00.000Z","formattedDate":"27 avril 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-27T00:00:00.000Z"},"nextItem":{"title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.6","permalink":"/fr/3_6_6"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityUnlock from \'./qualityUnlock.png\';\\n\\n\\n\\n\\n### D\xe9verrouiller la qualit\xe9 vid\xe9o\\n\\nMaintenant, le verrouillage par d\xe9faut de la qualit\xe9 vid\xe9o est lib\xe9r\xe9, et vous pouvez changer d\'\xe9tat en cliquant, et vous devez actualiser la page apr\xe8s avoir chang\xe9 d\'\xe9tat.\\n\\n<img src={qualityUnlock} alt=\\"qualityUnlock.png\\" width=\\"330\\" height=\\"320\\" />\\n\\n<WebStore />"},{"id":"/3_6_6","metadata":{"permalink":"/fr/3_6_6","source":"@site/i18n/fr/docusaurus-plugin-content-blog/3_6_6/index.mdx","title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.6","description":"### Correction du verrouillage de la qualit\xe9 vid\xe9o","date":"2022-04-11T00:00:00.000Z","formattedDate":"11 avril 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-11T00:00:00.000Z"},"prevItem":{"title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.7","permalink":"/fr/3_6_7"},"nextItem":{"title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.5","permalink":"/fr/3_6_5"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityLock from \'./qualityLock.png\';\\nimport defaultStyle from \'./defaultStyle.png\';\\n\\n\\n\\n\\n ### Correction du verrouillage de la qualit\xe9 vid\xe9o\\n\\n La fonction de verrouillage de la qualit\xe9 vid\xe9o \xe9tait fournie dans la version 3.6.3, mais lorsque Auto est s\xe9lectionn\xe9, la qualit\xe9 est toujours verrouill\xe9e et ne peut pas \xeatre ajust\xe9e dynamiquement. Ce probl\xe8me est maintenant r\xe9solu.\\n\\n<img src={qualityLock} alt=\\"qualityLock.png\\" width=\\"276\\" height=\\"389\\" />\\n\\n### Restaurer le style par d\xe9faut des sous-titres\\n\\nDes styles de sous-titres pr\xe9d\xe9finis \xe9taient fournis dans les versions pr\xe9c\xe9dentes, et le style par d\xe9faut des sous-titres est d\xe9sormais compatible avec YouTube.\\n\\nSi vous devez revenir au style par d\xe9faut, vous pouvez cliquer sur le bouton R\xe9initialiser le style.\\n\\n<img src={defaultStyle} alt=\\"defaultStyle.png\\" width=\\"340\\" height=\\"524\\" />\\n\\n<WebStore />"},{"id":"/3_6_5","metadata":{"permalink":"/fr/3_6_5","source":"@site/i18n/fr/docusaurus-plugin-content-blog/3_6_5/index.mdx","title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.5","description":"### Correction des sous-titres qui ne s\'enroulent pas","date":"2022-04-07T00:00:00.000Z","formattedDate":"7 avril 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-07T00:00:00.000Z"},"prevItem":{"title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.6","permalink":"/fr/3_6_6"},"nextItem":{"title":"Notes de mise \xe0 jour de YouTube\u2122 Dual Caption 3.6.0","permalink":"/fr/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport improveHandle from \'./improveHandle.gif\';\\nimport subtitleRightClickMenu from \'./subtitleRightClickMenu.gif\';\\nimport subtitleTips from \'./subtitleTips.png\';\\nimport subtitleWrap from \'./subtitleWrap.png\';\\n\\n\\n\\n\\n ### Correction des sous-titres qui ne s\'enroulent pas\\n\\n Correction d\'un probl\xe8me qui provoquait le d\xe9bordement des sous-titres sans habillage lorsque la police \xe9tait trop grande dans la version pr\xe9c\xe9dente.\\n\\n<img src={subtitleWrap} alt=\\"subtitleWrap.png\\" width=\\"600\\" height=\\"698\\" />\\n\\n### Correction de l\'invite de sous-titres\\n\\nDans les versions pr\xe9c\xe9dentes, l\'invite de sous-titres utilisait \xe9galement le style, et maintenant ce probl\xe8me est \xe9vit\xe9.\\n\\n<img src={subtitleTips} alt=\\"subtitleTips.png\\" width=\\"600\\" height=\\"682\\" />\\n\\n### Am\xe9liorer la poign\xe9e\\n\\nLes boutons de la poign\xe9e \xe9taient difficiles \xe0 faire glisser lorsque le sous-titre \xe9tait d\xe9plac\xe9 vers la bordure, il est maintenant plus facile \xe0 utiliser en changeant la position de la poign\xe9e.\\n\\n<img src={improveHandle} alt=\\"improveHandle.gif\\" width=\\"600\\" height=\\"338\\" />\\n\\n### Menu contextuel des sous-titres\\n\\nDans le pass\xe9, les sous-titres pouvaient \xeatre copi\xe9s, mais l\'op\xe9ration de clic droit ne pouvait pas \xeatre effectu\xe9e. Maintenant, ce probl\xe8me a \xe9t\xe9 r\xe9solu.\\n\\n<img src={subtitleRightClickMenu} alt=\\"subtitleRightClickMenu.gif\\" width=\\"600\\" height=\\"313\\" />\\n\\n<WebStore />"},{"id":"/3_6_0","metadata":{"permalink":"/fr/3_6_0","source":"@site/i18n/fr/docusaurus-plugin-content-blog/3_6_0/index.mdx","title":"Notes de mise \xe0 jour de YouTube\u2122 Dual Caption 3.6.0","description":"Correction de la fusion des d\xe9clarations \ud83c\udf89","date":"2021-12-16T00:00:00.000Z","formattedDate":"16 d\xe9cembre 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-12-16T00:00:00.000Z"},"prevItem":{"title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.6.5","permalink":"/fr/3_6_5"},"nextItem":{"title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.5.0","permalink":"/fr/3_5_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport mark from \'./mark.png\';\\nimport disableStyle from \'./disableStyle.gif\';\\nimport order from \'./order.gif\';\\n\\n\\n### Correction de la fusion des d\xe9clarations \ud83c\udf89\\n\\n Dans la version pr\xe9c\xe9dente, la fusion des d\xe9clarations n\'\xe9tait pas effectu\xe9e afin de suivre le comportement de YouTube.\\n\\nCela a rendu les phrases saccad\xe9es lors de la lecture de la vid\xe9o, et le t\xe9l\xe9chargement de sous-titres a \xe9galement encombr\xe9 la chronologie.\\n\\nDans la version 3.6.0, ce probl\xe8me a \xe9t\xe9 corrig\xe9 et plus de d\xe9tails ont \xe9t\xe9 optimis\xe9s.\\n\\nSuppression des espaces suppl\xe9mentaires dans les instructions de fusion.\\n\\n```\\n 1\\n 00:00:03,678 --\x3e 00:00:05,960\\n means that many aspects  of our lives\\n \u610f\u5473\u7740 \u6211\u4eec\u751f\u6d3b\u7684\u8bb8\u591a\u65b9\u9762\\n\\n 2\\n 00:00:05,960 --\x3e 00:00:08,214are regulated  by a weird entity.\\n \u90fd \u53d7\u5230\u4e00\u4e2a\u5947\u602a\u7684\u5b9e\u4f53\u7684\u76d1\u7ba1\u3002\\n\\n 3\\n 00:00:09,050 --\x3e 00:00:13,453It feels like a huge bureaucracy is making decisions over our heads.\\n \u611f\u89c9\u5c31\u50cf\u4e00\u4e2a\u5de8\u5927\u7684\u5b98\u50da\u673a\u6784 \u6b63\u5728\u6211\u4eec\u7684\u5934\u8111\u4e2d\u505a\u51fa\u51b3\u5b9a\u3002\\n ```\\n\\nSuppression de la chronologie vide pour le t\xe9l\xe9chargement des sous-titres.\\n\\n```\\n 1\\n 00:00:00,000 --\x3e 00:00:00,500\\n\\n\\n\\n 2\\n 00:00:00,500 --\x3e 00:00:03,090\\n PETE LEPAGE: You can now upload extensions using Manifest\\n PETE LEPAGE: \u60a8\u73b0\u5728\u53ef\u4ee5 \u4f7f\u7528 Manifest V3 \u5c06\u6269\u5c55\u7a0b\u5e8f\u4e0a\u4f20\\n\\n 3\\n 00:00:03,090 --\x3e 00:00:05,100\\n V3 to the Chrome Web Store.\\n \u5230 Chrome \u7f51\u4e0a\u5e94\u7528\u5e97\u3002\\n ```\\n\\n### Les sous-titres doubles donnent la priorit\xe9 aux sous-titres vid\xe9o\\n\\n Dans le pass\xe9, les sous-titres bilingues \xe9taient toujours traduits automatiquement. D\xe9sormais, les sous-titres existants de la vid\xe9o seront utilis\xe9s en premier, et la traduction automatique sera utilis\xe9e s\'il n\'y a pas de sous-titres.\\n\\n Si la vid\xe9o bilingue a d\xe9j\xe0 des sous-titres, un indicateur d\'optimisation s\'affichera.\\n\\n Le t\xe9l\xe9chargement des sous-titres aura \xe9galement la m\xeame optimisation, avec une coche au milieu du nom du fichier.\\n\\n<Tip>Le logo indique que les sous-titres bilingues actuels utilisent les sous-titres existants de la vid\xe9o, et non la traduction automatique.</Tip>\\n\\n<img src={mark} alt=\\"mark.png\\" width=\\"891\\" height=\\"262\\" />\\n\\n### D\xe9sactiver les styles personnalis\xe9s\\n\\n Cette version offre la possibilit\xe9 de d\xe9sactiver le style personnalis\xe9 et peut d\xe9finir l\'ordre et l\'espacement des sous-titres ind\xe9pendamment.\\n\\n<Tip>Astuce : Le principe de cette optimisation est que les deux sous-titres doivent correspondre exactement.</Tip>\\n\\n<img src={disableStyle} alt=\\"disableStyle.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n### T\xe9l\xe9chargez les sous-titres dans le m\xeame ordre qu\'ils sont affich\xe9s\\n\\n<img src={order} alt=\\"order.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n<WebStore />"},{"id":"/3_5_0","metadata":{"permalink":"/fr/3_5_0","source":"@site/i18n/fr/docusaurus-plugin-content-blog/3_5_0/index.mdx","title":"Notes de mise \xe0 jour YouTube\u2122 Dual Caption 3.5.0","description":"Style de police personnalis\xe9","date":"2021-08-24T00:00:00.000Z","formattedDate":"24 ao\xfbt 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-08-24T00:00:00.000Z"},"prevItem":{"title":"Notes de mise \xe0 jour de YouTube\u2122 Dual Caption 3.6.0","permalink":"/fr/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport light from \'./light.png\';\\nimport dark from \'./dark.png\';\\nimport tip from \'./tip.gif\';\\nimport singlebutton from \'./singlebutton.png\';\\nimport single from \'./single.png\';\\nimport translationError from \'./translationError.gif\';\\n\\n\\n### Style de police personnalis\xe9\\n\\nCliquez sur l\'ic\xf4ne d\'extension pour faire appara\xeetre la page des param\xe8tres afin de prendre en charge le mode sombre.\\n\\nOptions personnalis\xe9es\xa0: police, taille de police, couleur, couleur d\'arri\xe8re-plan, espacement et ordre des sous-titres.\\n\\n<img src={light} alt=\\"light.png\\" width=\\"340\\" height=\\"472\\" />\\n<img src={dark} alt=\\"dark.png\\" width=\\"340\\" height=\\"472\\" />\\n\\n<Tip>Passer la souris sur l\'ic\xf4ne r\xe9v\xe9lera ce que le param\xe8tre repr\xe9sente.</Tip>\\n\\n<img src={tip} alt=\\"tip.gif\\" width=\\"340\\" height=\\"472\\" />\\n\\n### Style de sous-titre unique\\n\\nApr\xe8s \xeatre pass\xe9 en mode sous-titre unique, le style de sous-titre unique peut \xeatre d\xe9fini ind\xe9pendamment.\\n\\n<img src={singlebutton} alt=\\"singlebutton.png\\" width=\\"314\\" height=\\"372\\" />\\n<img src={single} alt=\\"single.png\\" width=\\"340\\" height=\\"406\\" />\\n\\n### Autres optimisations\\n\\nSupprimez la biblioth\xe8que de programmes chinois traditionnel \xe0 simplifi\xe9, r\xe9duisez la taille du programme et am\xe9liorez les performances, car YouTube a officiellement corrig\xe9 ce bogue.\\n\\n<Tip>Parfois, les sous-titres manquent, principalement en raison d\'erreurs de traduction YouTube.</Tip>\\n\\n<img src={translationError} alt=\\"translationError.gif\\" width=\\"700\\" height=\\"396\\" />\\n\\n\xc0 l\'avenir, d\'autres moteurs de traduction seront introduits pour r\xe9soudre ce probl\xe8me et compenser l\'inexactitude de la traduction.\\n\\n<WebStore />"}]}')}}]);