"use strict";(self.webpackChunktwo=self.webpackChunktwo||[]).push([[32],{3023:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"/3_6_7","metadata":{"permalink":"/pt/3_6_7","source":"@site/i18n/pt/docusaurus-plugin-content-blog/3_6_7/index.mdx","title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.7","description":"Desbloqueie a qualidade do v\xeddeo","date":"2022-04-27T00:00:00.000Z","formattedDate":"27 de abril de 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-27T00:00:00.000Z"},"nextItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.6","permalink":"/pt/3_6_6"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityUnlock from \'./qualityUnlock.png\';\\n\\n\\n\\n\\n### Desbloqueie a qualidade do v\xeddeo\\n\\nAgora, o bloqueio padr\xe3o da qualidade do v\xeddeo \xe9 liberado e voc\xea pode alternar os estados clicando e precisa atualizar a p\xe1gina ap\xf3s alternar os estados.\\n\\n<img src={qualityUnlock} alt=\\"qualityUnlock.png\\" width=\\"330\\" height=\\"320\\" />\\n\\n<WebStore />"},{"id":"/3_6_6","metadata":{"permalink":"/pt/3_6_6","source":"@site/i18n/pt/docusaurus-plugin-content-blog/3_6_6/index.mdx","title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.6","description":"### Corrigir o bloqueio de qualidade de v\xeddeo","date":"2022-04-11T00:00:00.000Z","formattedDate":"11 de abril de 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-11T00:00:00.000Z"},"prevItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.7","permalink":"/pt/3_6_7"},"nextItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.5","permalink":"/pt/3_6_5"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport qualityLock from \'./qualityLock.png\';\\nimport defaultStyle from \'./defaultStyle.png\';\\n\\n\\n\\n\\n ### Corrigir o bloqueio de qualidade de v\xeddeo\\n\\n A fun\xe7\xe3o de bloqueio de qualidade de v\xeddeo foi fornecida na vers\xe3o 3.6.3, mas quando Autom\xe1tico \xe9 selecionado, a qualidade ainda est\xe1 bloqueada e n\xe3o pode ser ajustada dinamicamente.Este problema foi corrigido.\\n\\n<img src={qualityLock} alt=\\"qualityLock.png\\" width=\\"276\\" height=\\"389\\" />\\n\\n### Restaurar o estilo padr\xe3o da legenda\\n\\nEstilos de legendas predefinidos foram fornecidos em vers\xf5es anteriores, e o estilo padr\xe3o de legendas agora \xe9 consistente com o YouTube.\\n\\nSe voc\xea precisar reverter para o estilo padr\xe3o, clique no bot\xe3o Redefinir estilo.\\n\\n<img src={defaultStyle} alt=\\"defaultStyle.png\\" width=\\"340\\" height=\\"524\\" />\\n\\n<WebStore />"},{"id":"/3_6_5","metadata":{"permalink":"/pt/3_6_5","source":"@site/i18n/pt/docusaurus-plugin-content-blog/3_6_5/index.mdx","title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.5","description":"### Corrigir as legendas n\xe3o quebrando","date":"2022-04-07T00:00:00.000Z","formattedDate":"7 de abril de 2022","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2022-04-07T00:00:00.000Z"},"prevItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.6","permalink":"/pt/3_6_6"},"nextItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.0","permalink":"/pt/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport improveHandle from \'./improveHandle.gif\';\\nimport subtitleRightClickMenu from \'./subtitleRightClickMenu.gif\';\\nimport subtitleTips from \'./subtitleTips.png\';\\nimport subtitleWrap from \'./subtitleWrap.png\';\\n\\n\\n\\n\\n ### Corrigir as legendas n\xe3o quebrando\\n\\n Corrigido um problema que fazia com que as legendas transbordassem sem quebra quando a fonte era muito grande na vers\xe3o anterior.\\n\\n<img src={subtitleWrap} alt=\\"subtitleWrap.png\\" width=\\"600\\" height=\\"698\\" />\\n\\n### Corrigir o prompt de legenda\\n\\nNas vers\xf5es anteriores, o prompt de legendas tamb\xe9m usaria o estilo, e agora esse problema \xe9 evitado.\\n\\n<img src={subtitleTips} alt=\\"subtitleTips.png\\" width=\\"600\\" height=\\"682\\" />\\n\\n### Melhore a al\xe7a\\n\\nOs bot\xf5es da al\xe7a costumavam ser dif\xedceis de arrastar quando a legenda era arrastada para a borda, agora \xe9 mais f\xe1cil de usar alterando a posi\xe7\xe3o da al\xe7a.\\n\\n<img src={improveHandle} alt=\\"improveHandle.gif\\" width=\\"600\\" height=\\"338\\" />\\n\\n### Menu do bot\xe3o direito do mouse em legendas\\n\\nNo passado, as legendas podiam ser copiadas, mas a opera\xe7\xe3o de clique com o bot\xe3o direito n\xe3o podia ser executada. Agora este problema foi melhorado.\\n\\n<img src={subtitleRightClickMenu} alt=\\"subtitleRightClickMenu.gif\\" width=\\"600\\" height=\\"313\\" />\\n\\n<WebStore />"},{"id":"/3_6_0","metadata":{"permalink":"/pt/3_6_0","source":"@site/i18n/pt/docusaurus-plugin-content-blog/3_6_0/index.mdx","title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.0","description":"Corrigir a mesclagem de instru\xe7\xf5es \ud83c\udf89","date":"2021-12-16T00:00:00.000Z","formattedDate":"16 de dezembro de 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-12-16T00:00:00.000Z"},"prevItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.5","permalink":"/pt/3_6_5"},"nextItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.5.0","permalink":"/pt/3_5_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport mark from \'./mark.png\';\\nimport disableStyle from \'./disableStyle.gif\';\\nimport order from \'./order.gif\';\\n\\n\\n### Corrigir a mesclagem de instru\xe7\xf5es \ud83c\udf89\\n\\n Na vers\xe3o anterior, a mesclagem de declara\xe7\xf5es n\xe3o era feita para seguir o pr\xf3prio comportamento do YouTube.\\n\\nIsso fez com que as frases ficassem entrecortadas ao reproduzir o v\xeddeo, e o download de legendas tamb\xe9m fez com que a linha do tempo ficasse desordenada.\\n\\nNa vers\xe3o 3.6.0, esse problema foi corrigido e mais detalhes foram otimizados.\\n\\nEspa\xe7os extras removidos nas instru\xe7\xf5es de mesclagem.\\n\\n```\\n 1\\n 00:00:03,678 --\x3e 00:00:05,960\\n means that many aspects  of our lives\\n \u610f\u5473\u7740 \u6211\u4eec\u751f\u6d3b\u7684\u8bb8\u591a\u65b9\u9762\\n\\n 2\\n 00:00:05,960 --\x3e 00:00:08,214are regulated  by a weird entity.\\n \u90fd \u53d7\u5230\u4e00\u4e2a\u5947\u602a\u7684\u5b9e\u4f53\u7684\u76d1\u7ba1\u3002\\n\\n 3\\n 00:00:09,050 --\x3e 00:00:13,453It feels like a huge bureaucracy is making decisions over our heads.\\n \u611f\u89c9\u5c31\u50cf\u4e00\u4e2a\u5de8\u5927\u7684\u5b98\u50da\u673a\u6784 \u6b63\u5728\u6211\u4eec\u7684\u5934\u8111\u4e2d\u505a\u51fa\u51b3\u5b9a\u3002\\n ```\\n\\nRemovida a linha do tempo em branco para baixar legendas.\\n\\n```\\n 1\\n 00:00:00,000 --\x3e 00:00:00,500\\n\\n\\n\\n 2\\n 00:00:00,500 --\x3e 00:00:03,090\\n PETE LEPAGE: You can now upload extensions using Manifest\\n PETE LEPAGE: \u60a8\u73b0\u5728\u53ef\u4ee5 \u4f7f\u7528 Manifest V3 \u5c06\u6269\u5c55\u7a0b\u5e8f\u4e0a\u4f20\\n\\n 3\\n 00:00:03,090 --\x3e 00:00:05,100\\n V3 to the Chrome Web Store.\\n \u5230 Chrome \u7f51\u4e0a\u5e94\u7528\u5e97\u3002\\n ```\\n\\n### Legendas duplas d\xe3o prioridade \xe0s legendas de v\xeddeo\\n\\n Antigamente, as legendas bil\xedngues eram sempre traduzidas automaticamente, agora as legendas existentes do v\xeddeo ser\xe3o usadas primeiro e a tradu\xe7\xe3o autom\xe1tica ser\xe1 usada se n\xe3o houver legenda.\\n\\n Se o v\xeddeo bil\xedngue j\xe1 tiver legendas, um indicador de otimiza\xe7\xe3o ser\xe1 exibido.\\n\\n O download de legendas tamb\xe9m ter\xe1 a mesma otimiza\xe7\xe3o, com uma marca no meio do nome do arquivo.\\n\\n<Tip>O logotipo indica que as legendas bil\xedngues atuais usam as legendas existentes do v\xeddeo, n\xe3o a tradu\xe7\xe3o autom\xe1tica.</Tip>\\n\\n<img src={mark} alt=\\"mark.png\\" width=\\"891\\" height=\\"262\\" />\\n\\n### Desativar estilos personalizados\\n\\n Esta vers\xe3o oferece a op\xe7\xe3o de desativar o estilo personalizado e pode definir a ordem e o espa\xe7amento das legendas de forma independente.\\n\\n<Tip>Dica: A premissa dessa otimiza\xe7\xe3o \xe9 que as duas legendas devem corresponder exatamente.</Tip>\\n\\n<img src={disableStyle} alt=\\"disableStyle.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n### Baixe as legendas na mesma ordem em que s\xe3o exibidas\\n\\n<img src={order} alt=\\"order.gif\\" width=\\"340\\" height=\\"585\\" />\\n\\n<WebStore />"},{"id":"/3_5_0","metadata":{"permalink":"/pt/3_5_0","source":"@site/i18n/pt/docusaurus-plugin-content-blog/3_5_0/index.mdx","title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.5.0","description":"Estilo de fonte personalizado","date":"2021-08-24T00:00:00.000Z","formattedDate":"24 de agosto de 2021","tags":[],"hasTruncateMarker":false,"authors":[{"name":"ouweiya","title":"Web Development","url":"https://github.com/ouweiya","imageURL":"img/avatar.png","key":"ouweiya"}],"frontMatter":{"authors":"ouweiya","date":"2021-08-24T00:00:00.000Z"},"prevItem":{"title":"Notas de atualiza\xe7\xe3o do YouTube\u2122 Dual Caption 3.6.0","permalink":"/pt/3_6_0"}},"content":"import WebStore from \'/src/components/WebStore\';\\nimport Tip from \'/src/components/Tip\';\\nimport light from \'./light.png\';\\nimport dark from \'./dark.png\';\\nimport tip from \'./tip.gif\';\\nimport singlebutton from \'./singlebutton.png\';\\nimport single from \'./single.png\';\\nimport translationError from \'./translationError.gif\';\\n\\n\\n### Estilo de fonte personalizado\\n\\nClique no \xedcone da extens\xe3o para abrir a p\xe1gina de configura\xe7\xf5es para oferecer suporte ao modo escuro.\\n\\nOp\xe7\xf5es personalizadas: fonte, tamanho da fonte, cor, cor de fundo, espa\xe7amento e ordem das legendas.\\n\\n<img src={light} alt=\\"light.png\\" width=\\"340\\" height=\\"472\\" />\\n<img src={dark} alt=\\"dark.png\\" width=\\"340\\" height=\\"472\\" />\\n\\n<Tip>Passar o mouse sobre o \xedcone revelar\xe1 o que a configura\xe7\xe3o representa.</Tip>\\n\\n<img src={tip} alt=\\"tip.gif\\" width=\\"340\\" height=\\"472\\" />\\n\\n### Estilo de legenda \xfanica\\n\\nDepois de mudar para o modo de legenda simples, o estilo de legenda simples pode ser definido de forma independente.\\n\\n<img src={singlebutton} alt=\\"singlebutton.png\\" width=\\"314\\" height=\\"372\\" />\\n<img src={single} alt=\\"single.png\\" width=\\"340\\" height=\\"406\\" />\\n\\n### Outras otimiza\xe7\xf5es\\n\\nRemova a biblioteca de programas chin\xeas tradicional para simplificado, reduza o tamanho do programa e melhore o desempenho, porque o YouTube corrigiu oficialmente esse bug.\\n\\n<Tip>\xc0s vezes, as legendas est\xe3o faltando, principalmente devido a erros de tradu\xe7\xe3o do YouTube.</Tip>\\n\\n<img src={translationError} alt=\\"translationError.gif\\" width=\\"700\\" height=\\"396\\" />\\n\\nNo futuro, outros mecanismos de tradu\xe7\xe3o ser\xe3o introduzidos para resolver esse problema e compensar a imprecis\xe3o da tradu\xe7\xe3o.\\n\\n<WebStore />"}]}')}}]);